            action: 'add-public-keys',
            'publicKeys': [{
                'id': 'key1',
                'purposes': ['authentication'],
                'type': 'EcdsaSecp256k1VerificationKey2019',
                'publicKeyJwk': {...}
            }]
        });
        console.log(
            'Complete adding public keys:',
            JSON.stringify(didDocument, null, 2)
